import math
import sys

import motif_find

FAILED = "failed"

PASSED = "passed"

SEQ_DICT = {
    "tata_short": "CTATTAAG",
    "tata_seq_long": "TCGAATCCGTACGGTATTAAGTACGGCGCCTCGAATTCGAATCCGTACGGCGCCCCCCGTACGGCGCCTCGAAT",
    "just_long":
        '',
    "tata_very_long": '',
    "with cpg": "ATATCGATAT",
    "no cpg": "ACTGACTA"
}

VERY_LONG_TATA = ''
TO_MUCH_TATA = \
    ''

PROB_DICT = {
    "one": (0.1, 1),
    "zero": (0.1, 0),
    "more_likely_zero": (0.4, 0.1),
    "more_likely_one": (0.4, 0.9),

}

TSV_LIST = [r"tester\tata.tsv", r"tester\initial_emision.tsv", "tester\cpg.tsv"]
LONG_MOTIF_TSV = "tester\long_motif.tsv"

global tester_index
global arg


def print_result(name, output):
    global tester_index
    tester_index += 1
    print("%d Testing %s: %s" % (tester_index, name, output))


def configure_sys(alg, seq, tsv, prob_tuple):
    global arg
    new_sys = arg.copy() + ["--alg", alg, seq, tsv, str(prob_tuple[0]), str(prob_tuple[1])]
    sys.argv = new_sys


def format_things(seq, tsv_file, prob):
    return "seq name: %s - tsv file: %s - prob name: %s" % (seq, tsv_file, prob)


def test_viterbi():
    results = []
    headers = []

    print("#############################################\nTesting "
          "viterbi\n#############################################\n\n")
    alg = "viterbi"

    # Basic
    configure_sys(alg, SEQ_DICT["tata_seq_long"], TSV_LIST[0], PROB_DICT["more_likely_zero"])
    output = motif_find.main()
    print_result("viterbi runs and finish", PASSED if output else FAILED)

    # Length of output
    print_result("viterbi output same length as seq",
                 PASSED if len(output) == len(SEQ_DICT["tata_seq_long"]) else FAILED)

    # Can't have motif
    output = []
    for seq in SEQ_DICT:
        configure_sys(alg, SEQ_DICT[seq], TSV_LIST[0], PROB_DICT["zero"])
        o = motif_find.main()
        output.append(True if "M" not in o else False)

    print_result("no motif if q is zero", PASSED if all(output) else FAILED)

    # Test need to have motif but can't
    configure_sys(alg, SEQ_DICT["no cpg"], TSV_LIST[2], PROB_DICT["one"])
    output = motif_find.main()
    print_result("Ask for motif but not inside seq", PASSED if "M" not in output else FAILED)

    results.append(output)
    headers.append(format_things("cpg", TSV_LIST[2], "one"))

    # Test small cpg
    configure_sys(alg, SEQ_DICT["with cpg"], TSV_LIST[2], PROB_DICT["one"])
    output = motif_find.main()
    print_result("Small motif", PASSED if "MM" in output else FAILED)

    results.append(output)
    headers.append(format_things("with cpg", TSV_LIST[2], "one"))

    # Must have motif
    output = []
    number_of_motif = 0
    for seq in SEQ_DICT:
        configure_sys(alg, SEQ_DICT[seq], TSV_LIST[0], PROB_DICT["one"])
        o = motif_find.main()
        output.append(True if "MMMMMM" in o else False)
        number_of_motif = max(number_of_motif, o.count("MMMMMM"))

    print_result("must have motif if q is one", PASSED if all(output) else FAILED)
    print_result("must have just one motif", PASSED if number_of_motif == 1 else FAILED)

    # Testing  long seq
    configure_sys(alg, VERY_LONG_TATA, TSV_LIST[0], PROB_DICT["one"])
    output = motif_find.main()
    print_result("long seq", PASSED if "MMMMMM" in output else FAILED)

    # Testing very long seq
    configure_sys(alg, TO_MUCH_TATA, TSV_LIST[0], PROB_DICT["one"])
    output = motif_find.main()
    number_of_motif = max(number_of_motif, output.count("MMMMMM"))
    print_result("long seq (>10k)", PASSED if number_of_motif == 1 else FAILED)

    results.append(output)
    headers.append(format_things("TO_MUCH_TATA", TSV_LIST[0], "one"))

    # Testing very long motif
    configure_sys(alg, TO_MUCH_TATA, LONG_MOTIF_TSV, PROB_DICT["one"])
    output = motif_find.main()
    number_of_motif = max(number_of_motif, output.count("M" * 100))
    print_result("very long motif", PASSED if number_of_motif == 1 else FAILED)

    results.append(output)
    headers.append(format_things("TO_MUCH_TATA", "LONG_MOTIF_TSV", "one"))

    for seq in SEQ_DICT:
        for tsv in TSV_LIST:
            for prob in PROB_DICT:
                configure_sys(alg, SEQ_DICT[seq], tsv, PROB_DICT[prob])
                output = motif_find.main()
                results.append(output)
                headers.append(format_things(seq, tsv, prob))

    with open("viterbi_output.csv", "w") as output_file:
        output_file.write(",".join(headers))
        output_file.write("\n")
        output_file.write(",".join(results))


def test_backward_forward():
    results = []
    headers = []

    print("#############################################\nTesting backward "
          "forward\n#############################################\n\n")
    # Basic
    alg = "forward"
    configure_sys(alg, SEQ_DICT["tata_seq_long"], TSV_LIST[0], PROB_DICT["more_likely_zero"])
    output = motif_find.main()
    print_result("forward runs and finish", PASSED if output else FAILED)

    alg = "backward"
    configure_sys(alg, SEQ_DICT["tata_seq_long"], TSV_LIST[0], PROB_DICT["more_likely_zero"])
    output = motif_find.main()
    print_result("backward runs and finish", PASSED if output else FAILED)

    for seq in SEQ_DICT:
        for tsv in TSV_LIST:
            for prob in PROB_DICT:
                alg = "forward"
                configure_sys(alg, SEQ_DICT[seq], tsv, PROB_DICT[prob])
                output_forward = motif_find.main()

                alg = "backward"
                configure_sys(alg, SEQ_DICT[seq], tsv, PROB_DICT[prob])
                output_backward = motif_find.main()

                print_result("compare backward and forward results for seq: %s tsv: %s prob: %s" %
                             (seq, tsv, prob),
                             PASSED if math.isclose(output_backward, output_forward, rel_tol=1e-5)
                             else FAILED)

                results.append(output_backward)
                headers.append(format_things(seq, tsv, prob))

    # Testing very long seq
    alg = "forward"
    configure_sys(alg, TO_MUCH_TATA, TSV_LIST[0], PROB_DICT["one"])
    output_forward = motif_find.main()

    alg = "backward"
    configure_sys(alg, TO_MUCH_TATA, TSV_LIST[0], PROB_DICT["one"])
    output_backward = motif_find.main()
    print_result("Testing very long seq", PASSED if math.isclose(output_backward, output_forward,
                                                                 rel_tol=1e-5) else FAILED)

    results.append(output_forward)
    headers.append(format_things("TO_MUCH_TATA", TSV_LIST[0], "one"))

    # Testing very long motif
    alg = "forward"
    configure_sys(alg, TO_MUCH_TATA, LONG_MOTIF_TSV, PROB_DICT["one"])
    output_forward = motif_find.main()

    alg = "backward"
    configure_sys(alg, TO_MUCH_TATA, LONG_MOTIF_TSV, PROB_DICT["one"])
    output_backward = motif_find.main()
    print_result("Testing very long motif", PASSED if math.isclose(output_backward, output_forward,
                                                                   rel_tol=1e-5) else FAILED)

    results.append(output_forward)
    headers.append(format_things("TO_MUCH_TATA", "LONG_MOTIF_TSV", "one"))

    with open("forward_backward.csv", "w") as output_file:
        output_file.write(",".join(headers))
        output_file.write("\n")
        output_file.write(",".join([str(i) for i in results]))


def test_posterior():
    results = []
    headers = []

    print("#############################################\nTesting "
          "posterior\n#############################################\n\n")
    alg = "posterior"

    # Basic
    configure_sys(alg, SEQ_DICT["tata_seq_long"], TSV_LIST[0], PROB_DICT["more_likely_zero"])
    output = motif_find.main()
    print_result("posterior runs and finish", PASSED if output else FAILED)

    # Length of output
    print_result("posterior output same length as seq",
                 PASSED if len(output) == len(SEQ_DICT["tata_seq_long"]) else FAILED)

    for seq in SEQ_DICT:
        for tsv in TSV_LIST:
            for prob in PROB_DICT:
                configure_sys(alg, SEQ_DICT[seq], tsv, PROB_DICT[prob])
                output = motif_find.main()
                results.append(output)
                headers.append(format_things(seq, tsv, prob))

    # Testing very long seq
    configure_sys(alg, TO_MUCH_TATA, TSV_LIST[0], PROB_DICT["one"])
    output = motif_find.main()

    results.append(output)
    headers.append(format_things("TO_MUCH_TATA", TSV_LIST[0], "one"))

    # Testing very long motif
    configure_sys(alg, TO_MUCH_TATA, LONG_MOTIF_TSV, PROB_DICT["one"])
    output = motif_find.main()
    results.append(output)
    headers.append(format_things("TO_MUCH_TATA", "LONG_MOTIF_TSV", "one"))

    with open("posterior_output.csv", "w") as output_file:
        output_file.write(",".join(headers))
        output_file.write("\n")
        output_file.write(",".join(results))


def main():
    global tester_index
    global arg
    arg = sys.argv
    tester_index = 0
    #test_viterbi()
    test_backward_forward()
    #test_posterior()


if __name__ == '__main__':
    main()
